import { iPayment } from "./iPayment";
import { iReceiptProduct } from "./iReceiptProduct";
import { ReceiptStatus, ReceiptStatusKeys } from "./receiptStatus";


export interface iReceipt {
    id: number
    reg_date: string//дата регистрации чека
    status: typeof ReceiptStatus[ReceiptStatusKeys]
    //данные чека
    fn: string
    fd: string
    fp: string
    date: string | null//дата пробития чека на кассе
    sum: number//сумма чека
    //только для промодерированных чеков status != quote есть поля ниже
    address?: string
    address_name?: string
    inn?: string

    last_error?: string

    products: iReceiptProduct[] | null//товары в чеке
    payments: iPayment[] | null
    cashback_sum: number//сумма кэшбека
}